import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import {
  AppBar,
  Toolbar,
  Typography,
  IconButton,
  InputBase,
  Box,
  Menu,
  MenuItem,
  ListItemIcon,
  ListItemText,
} from "@mui/material";
import { 
  Menu as MenuIcon, 
  Search, 
  AccountCircle, 
  Logout, 
  Bookmarks, 
  Person, 
  SmartToy, 
  Login,
  Dashboard 
} from "@mui/icons-material";
import axios from "axios";

const Header = ({ toggleSidebar }) => {
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [anchorEl, setAnchorEl] = useState(null);
  const [userRole, setUserRole] = useState(null);
  const navigate = useNavigate();

  // Fetch user role on mount
  useEffect(() => {
    const fetchUserRole = () => {
      const storedToken = localStorage.getItem("token") || sessionStorage.getItem("token");
      const role = localStorage.getItem("role") || sessionStorage.getItem("role");
      if (storedToken) {
        setUserRole(role);
      }
    };

    fetchUserRole();
  }, []);

  const handleSearch = async (event) => {
    setSearchQuery(event.target.value);
    if (event.target.value.length > 2) {
      try {
        const response = await axios.get(`http://localhost:8080/api/search?query=${event.target.value}`);
        setSearchResults(response.data);
      } catch (error) {
        console.error("Error fetching search results:", error);
      }
    } else {
      setSearchResults([]);
    }
  };

  const handleProfileClick = (event) => {
    if (userRole) {
      setAnchorEl(event.currentTarget);
    } else {
      navigate("/login");
    }
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleLogout = () => {
    handleClose();
    sessionStorage.clear();
    localStorage.clear();
    setUserRole(null);
    navigate("/login");
  };

  return (
    <AppBar position="fixed" sx={{ backgroundColor: "#e65100", zIndex: 1201 }}>
      <Toolbar>
        <IconButton edge="start" color="inherit" aria-label="menu" onClick={toggleSidebar} sx={{ mr: 2 }}>
          <MenuIcon />
        </IconButton>

        <Typography variant="h6" sx={{ flexGrow: 1, fontWeight: "bold" }}>
          AptitudeArc
        </Typography>

        {/* Search Bar */}
        <Box sx={{ display: "flex", alignItems: "center", backgroundColor: "#ffffff33", borderRadius: 1, px: 2, py: 0.5 }}>
          <Search sx={{ color: "#fff" }} />
          <InputBase
            placeholder="Search..."
            value={searchQuery}
            onChange={handleSearch}
            sx={{ ml: 1, color: "#fff" }}
          />
        </Box>

        {/* Profile / Login Icon */}
        <IconButton color="inherit" sx={{ ml: 2 }} onClick={handleProfileClick}>
          {userRole ? <AccountCircle /> : <Login />}
        </IconButton>

        {/* Profile Menu for Logged-in Users */}
        {userRole && (
          <Menu anchorEl={anchorEl} open={Boolean(anchorEl)} onClose={handleClose}>
            {userRole === "ADMIN" ? (
              <MenuItem onClick={() => navigate("/admin/dashboard")}>
                <ListItemIcon>
                  <Dashboard fontSize="small" />
                </ListItemIcon>
                <ListItemText primary="Admin Dashboard" />
              </MenuItem>
            ) : (
              <>
                <MenuItem onClick={() => navigate("/user/profile")}>
                  <ListItemIcon>
                    <Person fontSize="small" />
                  </ListItemIcon>
                  <ListItemText primary="Profile Page" />
                </MenuItem>
                <MenuItem onClick={() => navigate("/ai")}>
                  <ListItemIcon>
                    <SmartToy fontSize="small" />
                  </ListItemIcon>
                  <ListItemText primary="AI" />
                </MenuItem>
                <MenuItem onClick={() => navigate("/bookmarks")}>
                  <ListItemIcon>
                    <Bookmarks fontSize="small" />
                  </ListItemIcon>
                  <ListItemText primary="Bookmark Page" />
                </MenuItem>
              </>
            )}
            
            <MenuItem onClick={handleLogout}>
              <ListItemIcon>
                <Logout fontSize="small" />
              </ListItemIcon>
              <ListItemText primary="Logout" />
            </MenuItem>
          </Menu>
        )}
      </Toolbar>

      {/* Display Search Results */}
      {searchResults.length > 0 && (
        <Box sx={{ position: "absolute", top: "56px", left: "50%", transform: "translateX(-50%)", width: "50%", backgroundColor: "#fff", borderRadius: 1, boxShadow: 3, p: 2 }}>
          {searchResults.map((result, index) => (
            <Typography key={index} variant="body1" sx={{ padding: "8px 0", cursor: "pointer" }}>
              {result.question}
            </Typography>
          ))}
        </Box>
      )}
    </AppBar>
  );
};

export default Header;
