import React, { useState } from 'react';

const Home = () => {
  // Initialize state with categories data
  const [categories, setCategories] = useState([
    { title: 'General Aptitude', items: ['Arithmetic Aptitude', 'Data Interpretation', 'Online Aptitude Test', 'Data Interpretation Test'] },
    { title: 'Verbal and Reasoning', items: ['Verbal Ability', 'Logical Reasoning', 'Verbal Reasoning', 'Non-Verbal Reasoning'] },
    // Add other categories as needed
  ]);

  // Function to add a new category
  const addCategory = (title) => {
    setCategories([...categories, { title, items: [] }]);
  };

  // Function to remove a category
  const removeCategory = (index) => {
    const newCategories = categories.filter((_, i) => i !== index);
    setCategories(newCategories);
  };

  // Function to add an item to a category
  const addItemToCategory = (index, item) => {
    const newCategories = [...categories];
    newCategories[index].items.push(item);
    setCategories(newCategories);
  };

  // Function to remove an item from a category
  const removeItemFromCategory = (categoryIndex, itemIndex) => {
    const newCategories = [...categories];
    newCategories[categoryIndex].items.splice(itemIndex, 1);
    setCategories(newCategories);
  };

  return (
    <div>
      <h1>Categories</h1>
      <div>
        {categories.map((category, categoryIndex) => (
          <div key={categoryIndex}>
            <h2>{category.title}</h2>
            <button onClick={() => removeCategory(categoryIndex)}>Remove Category</button>
            <ul>
              {category.items.map((item, itemIndex) => (
                <li key={itemIndex}>
                  {item}
                  <button onClick={() => removeItemFromCategory(categoryIndex, itemIndex)}>Remove Item</button>
                </li>
              ))}
            </ul>
            <button onClick={() => addItemToCategory(categoryIndex, 'New Item')}>Add Item</button>
          </div>
        ))}
      </div>
      <button onClick={() => addCategory('New Category')}>Add Category</button>
    </div>
  );
};

export default Home;
