import React from "react";
import { Container, Grid, Paper, Typography, Box } from "@mui/material";

const categories = [
  { title: "General Aptitude", items: ["Arithmetic Aptitude", "Data Interpretation", "Online Tests"] },
  { title: "Interview", items: ["Placement Papers", "Group Discussion", "HR Interview"] },
  { title: "Engineering", items: ["Mechanical", "Civil", "CSE", "EEE"] },
  { title: "Programming", items: ["C", "C++", "Java", "Python"] },
];

const Home = () => {
  return (
    <Container sx={{ mt: 3 }}>
      <Typography variant="h4" sx={{ mb: 2, color: "orange", fontWeight: "bold" }}>
        Welcome to AptitudeArc!
      </Typography>
      <Grid container spacing={3}>
        {categories.map((category, index) => (
          <Grid item xs={12} md={6} lg={4} key={index}>
            <Paper
              sx={{
                padding: 2,
                backgroundColor: "#fff8e1", // Light orange background
                borderLeft: "6px solid orange", // Orange border
                borderRadius: 2,
                transition: "0.3s",
                "&:hover": { transform: "scale(1.05)", boxShadow: 3 },
              }}
            >
              <Typography variant="h6" sx={{ color: "orange" }}>
                {category.title}
              </Typography>
              <ul>
                {category.items.map((item, i) => (
                  <li key={i}>{item}</li>
                ))}
              </ul>
            </Paper>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default Home;
