import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { Box } from "@mui/material";
import Header from "./components/Header";
import Sidebar from "./components/Sidebar";
import Home from "./pages/Home";
import GeneralAptitude from "./pages/GeneralAptitude";
import Interview from "./pages/Interview";
import Engineering from "./pages/Engineering";

const App = () => {
  const [openSidebar, setOpenSidebar] = useState(true);

  const handleSidebarToggle = () => {
    setOpenSidebar(!openSidebar);
  };

  return (
    <Router>
      <Box sx={{ display: "flex" }}>
        {/* Header with Sidebar Toggle Button */}
        <Header toggleSidebar={handleSidebarToggle} />

        {/* Sidebar with Toggle Function */}
        <Sidebar openSidebar={openSidebar} toggleSidebar={handleSidebarToggle} />

        {/* Main Content */}
        <Box component="main" sx={{ flexGrow: 1, p: 3, mt: 8 }}>
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/general-aptitude" element={<GeneralAptitude />} />
            <Route path="/interview" element={<Interview />} />
            <Route path="/engineering" element={<Engineering />} />
          </Routes>
        </Box>
      </Box>
    </Router>
  );
};

export default App;
