import React, { useState } from "react";
import {
  Container, Typography, Breadcrumbs, Link, Paper, FormControl, FormControlLabel,
  RadioGroup, Radio, Button, Pagination, Box, Divider
} from "@mui/material";
import { useParams } from "react-router-dom";

const TopicDetail = () => {
  const { topic } = useParams();

  // Ensure topic exists before calling .replace()
  const formattedTopic = topic ? topic.replace(/-/g, " ") : "Unknown Topic";

  return (
    <Container sx={{ mt: 3 }}>
      {/* Breadcrumbs */}
      <Breadcrumbs aria-label="breadcrumb">
        <Link underline="hover" color="inherit" href="/">Home</Link>
        <Link underline="hover" color="inherit" href="/topic">Aptitude</Link>
        <Typography color="textPrimary">{formattedTopic}</Typography>
      </Breadcrumbs>

      {/* Topic Title */}
      <Typography variant="h4" sx={{ my: 2, fontWeight: "bold", textTransform: "capitalize" }}>
        {formattedTopic} - General Questions
      </Typography>

      <Typography variant="body1">Coming Soon...</Typography>

      {/* Footer Section */}
      <Divider sx={{ my: 3 }} />
      <Typography variant="body2" sx={{ textAlign: "center", color: "gray" }}>
        Â© AptitudeArc Technologies |
        <Link href="#" sx={{ ml: 1 }}>Contact Us</Link> |
        <Link href="#" sx={{ ml: 1 }}>Copyright</Link> |
        <Link href="#" sx={{ ml: 1 }}>Privacy Policy</Link>
      </Typography>
    </Container>
  );
};

export default TopicDetail;
