import React, { useEffect, useState } from "react";
import { Container, Grid, Paper, Typography, Breadcrumbs, Link, TextField, Box } from "@mui/material";
import { Folder } from "@mui/icons-material";
import { useParams, useNavigate } from "react-router-dom";

// Define topics for each category
const topicsByCategory = {
  "Arithmetic": ["Problems on Trains", "Simple Interest", "Percentage", "Average", "Numbers"],
  "Java": ["Language Fundamentals", "Objects and Collections", "Operators and Assignments", "Threads", "Inner Classes"],
  "Mechanical": ["Engineering Mechanics", "Strength of Materials", "Hydraulics and Fluid Mechanics", "Thermodynamics"],
  "Data Interpretation": ["Table Charts", "Bar Charts", "Pie Charts", "Line Charts"],
};

const TopicPage = () => {
  const { category } = useParams();  // Get category from URL
  const navigate = useNavigate();
  const [filteredTopics, setFilteredTopics] = useState([]);

  // Format category name to match topicsByCategory keys
  const formattedCategory = category
    ? category.replace(/-/g, " ").replace(/\b\w/g, (char) => char.toUpperCase())
    : "Aptitude";

  useEffect(() => {
    console.log("Category from URL:", category);
    console.log("Formatted Category:", formattedCategory);
    console.log("Available categories:", Object.keys(topicsByCategory));

    if (formattedCategory in topicsByCategory) {
      setFilteredTopics(topicsByCategory[formattedCategory]);
    } else {
      setFilteredTopics([]);
    }
  }, [category]);

  const handleTopicClick = (topic) => {
    const formattedTopic = topic.toLowerCase().replace(/\s+/g, "-");
    navigate(`/topic/${formattedTopic}`);
  };

  return (
    <Container sx={{ mt: 3, mb: 2 }}>
      {/* Breadcrumb Navigation */}
      <Breadcrumbs aria-label="breadcrumb" sx={{ mb: 2 }}>
        <Link underline="hover" color="inherit" href="/"> Home </Link>
        <Link underline="hover" color="inherit" href="/category"> Categories </Link>
        <Typography color="textPrimary">{formattedCategory}</Typography>
      </Breadcrumbs>

      {/* Title */}
      <Typography variant="h4" sx={{ mb: 3, fontWeight: "bold", color: "#e65100" }}>
        {formattedCategory} Questions and Answers
      </Typography>

      <Grid container spacing={3}>
        {/* Left Section (Topics List) */}
        <Grid item xs={12} md={9}>
          <Paper sx={{ p: 3 }} elevation={3}>
            <TextField fullWidth variant="outlined" placeholder="🔍 Search topics..." sx={{ mb: 3 }} />
            <Grid container spacing={2}>
              {filteredTopics.length > 0 ? (
                filteredTopics.map((topic, index) => (
                  <Grid
                    item xs={12} sm={6} md={4} key={index}
                    onClick={() => handleTopicClick(topic)}
                  >
                    <Box
                      sx={{
                        display: "flex",
                        alignItems: "center",
                        cursor: "pointer",
                        p: 1,
                        transition: "0.3s",
                        borderRadius: 1,
                        '&:hover': { color: "blue", backgroundColor: "#f5f5f5" }
                      }}
                    >
                      <Folder sx={{ color: "#e65100", mr: 1 }} />
                      <Typography>{topic}</Typography>
                    </Box>
                  </Grid>
                ))
              ) : (
                <Typography variant="h6" sx={{ textAlign: "center", color: "gray" }}>
                  No topics available for {formattedCategory}.
                </Typography>
              )}
            </Grid>
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
};

export default TopicPage;
