import React from "react";
import { AppBar, Toolbar, Typography, IconButton, InputBase, Box } from "@mui/material";
import { Menu, Search, AccountCircle } from "@mui/icons-material";

const Header = ({ toggleSidebar, isSidebarOpen }) => {
  return (
    <AppBar
      position="fixed"
      sx={{
        backgroundColor: "#e65100",
        zIndex: 1201, // Ensure it's above the sidebar
        width: isSidebarOpen ? `calc(100% - 240px)` : "100%", // Adjust width based on sidebar
        ml: isSidebarOpen ? "240px" : "0", // Shift when sidebar is open
        transition: "margin 0.3s ease, width 0.3s ease",
      }}
    >
      <Toolbar>
        {/* Sidebar Toggle Button */}
        <IconButton edge="start" color="inherit" aria-label="menu" onClick={toggleSidebar} sx={{ mr: 2 }}>
          <Menu />
        </IconButton>

        {/* App Name */}
        <Typography variant="h6" sx={{ flexGrow: 1, fontWeight: "bold" }}>
          AptitudeArc
        </Typography>

        {/* Search Bar */}
        <Box sx={{ display: "flex", alignItems: "center", backgroundColor: "#ffffff33", borderRadius: 1, px: 2, py: 0.5 }}>
          <Search sx={{ color: "#fff" }} />
          <InputBase placeholder="Search..." sx={{ ml: 1, color: "#fff" }} />
        </Box>

        {/* Profile Icon */}
        <IconButton color="inherit" sx={{ ml: 2 }}>
          <AccountCircle />
        </IconButton>
      </Toolbar>
    </AppBar>
  );
};

export default Header;
