import React from "react";
import { Drawer, List, ListItem, ListItemIcon, ListItemText, Box, IconButton } from "@mui/material";
import { Home, Calculate, School, EventNote, Menu } from "@mui/icons-material";
import { Link } from "react-router-dom";
import logo from "../assets/AptitudeArc.jpg";

const Sidebar = ({ openSidebar, toggleSidebar }) => {
  return (
    <Drawer
      variant="persistent"
      open={openSidebar}
      sx={{
        width: openSidebar ? 240 : 0,
        flexShrink: 0,
        "& .MuiDrawer-paper": {
          width: 240,
          transition: "width 0.3s",
          backgroundColor: "#fff8e1", // Light orange background
        },
      }}
    >
      {/* Logo and Menu Button */}
      <Box sx={{ display: "flex", alignItems: "center", justifyContent: "space-between", p: 2 }}>
        <img src={logo} alt="AptitudeArc Logo" style={{ width: "70%", maxWidth: "150px", borderRadius: "8px" }} />
        <IconButton onClick={toggleSidebar} sx={{ color: "orange" }}>
          <Menu />
        </IconButton>
      </Box>

      {/* Navigation List */}
      <List>
        <ListItem button component={Link} to="/">
          <ListItemIcon><Home sx={{ color: "orange" }} /></ListItemIcon>
          <ListItemText primary="Home" />
        </ListItem>
        <ListItem button component={Link} to="/general-aptitude">
          <ListItemIcon><Calculate sx={{ color: "orange" }} /></ListItemIcon>
          <ListItemText primary="General Aptitude" />
        </ListItem>
        <ListItem button component={Link} to="/interview">
          <ListItemIcon><EventNote sx={{ color: "orange" }} /></ListItemIcon>
          <ListItemText primary="Interview" />
        </ListItem>
        <ListItem button component={Link} to="/engineering">
          <ListItemIcon><School sx={{ color: "orange" }} /></ListItemIcon>
          <ListItemText primary="Engineering" />
        </ListItem>
      </List>
    </Drawer>
  );
};

export default Sidebar;
