import React from 'react';
import { useParams } from 'react-router-dom';

const ItemPage = () => {
  const { categoryName, itemName } = useParams();

  // Sample data structure to simulate category and item details
  const categories = [
    {
      title: 'General Aptitude',
      items: [
        { name: 'Arithmetic Aptitude', description: 'Description for Arithmetic Aptitude' },
        { name: 'Data Interpretation', description: 'Description for Data Interpretation' },
        // Add other items as needed
      ],
    },
    // Define other categories similarly
  ];

  // Find the category and item based on URL parameters
  const category = categories.find((cat) => cat.title.toLowerCase().replace(/\s+/g, '-') === categoryName);
  const item = category?.items.find((itm) => itm.name.toLowerCase().replace(/\s+/g, '-') === itemName);

  return (
    <div className="item-page">
      {item ? (
        <>
          <h1>{item.name}</h1>
          <p>{item.description}</p>
          {/* Add more details as needed */}
        </>
      ) : (
        <p>Item not found.</p>
      )}
    </div>
  );
};

export default ItemPage;
