import React from "react";
import { Container, Grid, Paper, Typography, Toolbar, Box } from "@mui/material";
import { Calculate, Work, School, Code } from "@mui/icons-material";

const categories = [
  { title: "General Aptitude", icon: <Calculate sx={{ color: "orange", fontSize: 40 }} />, items: ["Arithmetic Aptitude", "Data Interpretation", "Online Tests"] },
  { title: "Interview", icon: <Work sx={{ color: "orange", fontSize: 40 }} />, items: ["Placement Papers", "Group Discussion", "HR Interview"] },
  { title: "Engineering", icon: <School sx={{ color: "orange", fontSize: 40 }} />, items: ["Mechanical", "Civil", "CSE", "EEE"] },
  { title: "Programming", icon: <Code sx={{ color: "orange", fontSize: 40 }} />, items: ["C", "C++", "Java", "Python"] },
];

const Home = ({ openSidebar }) => {
  // Handle click event
  const handleItemClick = (category, item) => {
    console.log(`Clicked on: ${category} - ${item}`);
    alert(`You clicked on ${item} in ${category}`);
    // You can also navigate to a different page if needed
    // Example: navigate(`/category/${item.toLowerCase()}`);
  };

  return (
    <Box sx={{ flexGrow: 1, ml: openSidebar ? "240px" : "0px", transition: "margin 0.3s", pt: "80px" }}>
      {/* Push content below fixed header */}
      <Toolbar />

      <Container>
        <Typography variant="h4" sx={{ mb: 4, color: "#e65100", fontWeight: "bold", textAlign: "center" }}>
          Welcome to AptitudeArc!
        </Typography>

        <Grid container spacing={4} justifyContent="center" alignItems="center">
          {categories.map((category, index) => (
            <Grid item xs={12} sm={6} md={4} lg={3} key={index}>
              <Paper
                elevation={6}
                sx={{
                  padding: 3,
                  textAlign: "center",
                  backgroundColor: "#fff8e1",
                  borderRadius: 3,
                  transition: "0.3s",
                  display: "flex",
                  flexDirection: "column",
                  alignItems: "center",
                  "&:hover": { transform: "scale(1.05)", boxShadow: 6, backgroundColor: "#ffecb3" },
                }}
              >
                {/* Category Icon */}
                {category.icon}

                {/* Category Title */}
                <Typography variant="h6" sx={{ mt: 1, color: "#e65100", fontWeight: "bold" }}>
                  {category.title}
                </Typography>

                {/* Category Items */}
                <ul style={{ textAlign: "left", padding: "10px", listStyleType: "circle", cursor: "pointer" }}>
                  {category.items.map((item, i) => (
                    <li
                      key={i}
                      style={{ fontSize: "14px", padding: "5px 0", color: "#e65100" }}
                      onClick={() => handleItemClick(category.title, item)}
                    >
                      {item}
                    </li>
                  ))}
                </ul>
              </Paper>
            </Grid>
          ))}
        </Grid>
      </Container>
    </Box>
  );
};

export default Home;
