import React, { useState } from 'react';
import './style/Arithmetic.css';  // Link to the CSS file

const Arithmetic = () => {
  const [num1, setNum1] = useState('');
  const [num2, setNum2] = useState('');
  const [result, setResult] = useState(null);

  const handleCalculation = (operation) => {
    const n1 = parseFloat(num1);
    const n2 = parseFloat(num2);

    if (isNaN(n1) || isNaN(n2)) {
      alert('Please enter valid numbers');
      return;
    }

    let res;
    switch (operation) {
      case 'add':
        res = n1 + n2;
        break;
      case 'subtract':
        res = n1 - n2;
        break;
      case 'multiply':
        res = n1 * n2;
        break;
      case 'divide':
        if (n2 === 0) {
          alert('Cannot divide by zero');
          return;
        }
        res = n1 / n2;
        break;
      default:
        break;
    }

    setResult(res);
  };

  return (
    <div className="arithmetic-container">
      <h2>Arithmetic Operations</h2>
      <div className="input-container">
        <input
          type="number"
          value={num1}
          onChange={(e) => setNum1(e.target.value)}
          placeholder="Enter first number"
        />
        <input
          type="number"
          value={num2}
          onChange={(e) => setNum2(e.target.value)}
          placeholder="Enter second number"
        />
      </div>
      <div className="buttons-container">
        <button onClick={() => handleCalculation('add')}>Add</button>
        <button onClick={() => handleCalculation('subtract')}>Subtract</button>
        <button onClick={() => handleCalculation('multiply')}>Multiply</button>
        <button onClick={() => handleCalculation('divide')}>Divide</button>
      </div>
      {result !== null && <div className="result">Result: {result}</div>}
    </div>
  );
};

export default Arithmetic;
