import React from "react";
import { Container, Typography, Breadcrumbs, Link, Paper } from "@mui/material";
import { useParams } from "react-router-dom";

const TopicDetail = () => {
  const { topic } = useParams();
  const formattedTopic = topic.replace(/-/g, " ");

  return (
    <Container sx={{ mt: 3 }}>
      {/* Breadcrumbs */}
      <Breadcrumbs aria-label="breadcrumb">
        <Link underline="hover" color="inherit" href="/"> Home </Link>
        <Link underline="hover" color="inherit" href="/topic"> Aptitude </Link>
        <Typography color="textPrimary"> {formattedTopic} </Typography>
      </Breadcrumbs>

      {/* Topic Title */}
      <Typography variant="h4" sx={{ my: 2, fontWeight: "bold", textTransform: "capitalize" }}>
        {formattedTopic} Questions and Answers
      </Typography>

      {/* Topic Content */}
      <Paper sx={{ p: 3, mt: 2 }} elevation={3}>
        <Typography>
          Content related to <strong>{formattedTopic}</strong> will be displayed here.
        </Typography>
      </Paper>
    </Container>
  );
};

export default TopicDetail;
