import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { Box } from "@mui/material";
import Header from "./components/Header";
import Sidebar from "./components/Sidebar";
import AdminSidebar from "./components/AdminSidebar";
import Home from "./components/Home";
import TopicPage from "./pages/TopicPage";
import TopicDetail from "./pages/TopicDetail";
import LoginPage from "./auth/LoginPage";
import RegisterPage from "./auth/RegisterPage";
import ForgotPasswordPage from "./auth/ForgotPasswordPage";
import ProfilePage from "./pages/ProfilePage";
import AdminDashboard from "./pages/AdminDashboard";
import AdminRoute from "./routes/AdminRoute";
import UserRoute from "./routes/UserRoute";

const App = () => {
  const [openSidebar, setOpenSidebar] = useState(true);
  const [role, setRole] = useState(null);

  // Fetch role from sessionStorage on component mount
  useEffect(() => {
    const storedRole = sessionStorage.getItem("role");
    setRole(storedRole);
  }, []);

  const handleSidebarToggle = () => {
    setOpenSidebar(!openSidebar);
  };

  return (
    <Router>
      <Box sx={{ display: "flex" }}>
        {/* Header (Visible for all) */}
        <Header toggleSidebar={handleSidebarToggle} />

        {/* Sidebar - Show based on role */}
        {role === "ADMIN" ? (
          <AdminSidebar openSidebar={openSidebar} />
        ) : role === "USER" || role === null ? (
          <Sidebar openSidebar={openSidebar} />
        ) : null}

        {/* Main Content */}
        <Box
          component="main"
          sx={{
            flexGrow: 1,
            p: 3,
            ml: openSidebar ? "240px" : "0px", // Adjust margin when sidebar is open
            mt: "64px", // Prevent overlap with Header
            transition: "margin 0.3s",
          }}
        >
          <Routes>
            {/* Public Routes (Guest Users) */}
            <Route path="/" element={<Home openSidebar={openSidebar} />} />
            <Route path="/topic" element={<TopicPage />} />
            <Route path="/topic/:category" element={<TopicPage />} />
            <Route path="/topic/:category/:topic" element={<TopicDetail />} />
            <Route path="/login" element={<LoginPage />} />
            <Route path="/register" element={<RegisterPage />} />
            <Route path="/forgot-password" element={<ForgotPasswordPage />} />

            {/* User Routes */}
            <Route element={<UserRoute />}>
              <Route path="/user/profile" element={<ProfilePage />} />
            </Route>

            {/* Admin Routes */}
            <Route element={<AdminRoute />}>
              <Route path="/admin" element={<AdminDashboard />} />
            </Route>
          </Routes>
        </Box>
      </Box>
    </Router>
  );
};

export default App;
