import React from "react";
import {
  Container,
  Typography,
  Paper,
  Grid,
  Card,
  CardContent,
  Box,
  List,
  ListItemButton,
  ListItemText,
  Divider,
} from "@mui/material";
import { useNavigate } from "react-router-dom";
import CalculateIcon from "@mui/icons-material/Calculate";
import WorkIcon from "@mui/icons-material/Work";
import SchoolIcon from "@mui/icons-material/School";

const categories = [
  {
    title: "User Management",
    icon: <CalculateIcon sx={{ color: "orange", fontSize: 40 }} />,
    items: ["Manage user accounts", "Permissions", "Roles"],
  },
  {
    title: "Test Management",
    icon: <WorkIcon sx={{ color: "orange", fontSize: 40 }} />,
    items: ["Oversee and manage tests", "Assessments", "Evaluations"],
  },
  {
    title: "Analytics",
    icon: <SchoolIcon sx={{ color: "orange", fontSize: 40 }} />,
    items: ["View detailed analytics", "Insights for better decision making"],
  },
];

const AdminDashboard = () => {
  const navigate = useNavigate();

  // Handle click event for individual items
  const handleItemClick = (item) => {
    const formattedItem = item.toLowerCase().replace(/\s+/g, "-");
    console.log(`Navigating to: /topic/${formattedItem}`); // Debugging log
    navigate(`/topic/${formattedItem}`);
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 5 }}>
      <Paper
        elevation={6}
        sx={{
          p: 4,
          borderRadius: 3,
          backgroundColor: "#fff3e0", // Light orange background
        }}
      >
        <Typography
          variant="h4"
          align="center"
          sx={{ mb: 4, fontWeight: "bold", color: "#e65100" }}
        >
          Admin Dashboard
        </Typography>
        <Grid container spacing={4}>
          {categories.map((category, index) => (
            <Grid item xs={12} md={4} key={index}>
              <Card
                sx={{
                  border: "2px solid #e65100",
                  borderRadius: 2,
                  transition: "transform 0.2s, box-shadow 0.2s",
                  "&:hover": {
                    transform: "scale(1.02)",
                    boxShadow: "0px 6px 16px rgba(230, 81, 0, 0.4)",
                  },
                }}
              >
                <CardContent>
                  <Box
                    sx={{
                      display: "flex",
                      alignItems: "center",
                      mb: 2,
                    }}
                  >
                    {category.icon}
                    <Typography
                      variant="h6"
                      sx={{
                        ml: 1,
                        color: "#e65100",
                        fontWeight: "bold",
                      }}
                    >
                      {category.title}
                    </Typography>
                  </Box>
                  <List>
                    {category.items.map((item, idx) => (
                      <Box key={idx}>
                        <ListItemButton
                          onClick={() => handleItemClick(item)}
                          sx={{
                            borderRadius: 1,
                            transition: "background-color 0.2s",
                            "&:hover": { backgroundColor: "#ffe0b2" },
                          }}
                        >
                          <ListItemText primary={item} />
                        </ListItemButton>
                        {idx !== category.items.length - 1 && <Divider />}
                      </Box>
                    ))}
                  </List>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Paper>
    </Container>
  );
};

export default AdminDashboard;
