import React from "react";
import { 
  Container, Grid, Paper, Typography, Breadcrumbs, Link, TextField, Box} from "@mui/material";
import { Folder } from "@mui/icons-material";
import { useNavigate } from "react-router-dom";

const topics = [
  "Problems on Trains", "Simple Interest", "Percentage", "Average", "Numbers"
];

const quickLinks = {
  "General Aptitude": ["Arithmetic", "Data Interpretation", "Online Tests"],
  "Programming": ["Python Programming", "C Programming", "C++, C#", "Java"],
  "Interview": ["GD", "HR", "Technical Interview"],
  "Engineering": ["Mechanical", "Civil", "CSE", "EEE"]
};

const TopicPage = () => {
  const navigate = useNavigate();

  return (
    <Container sx={{ mt: 3, mb: 5 }}>
      {/* Breadcrumb Navigation */}
      <Breadcrumbs aria-label="breadcrumb" sx={{ mb: 2 }}>
        <Link underline="hover" color="inherit" href="/"> Home </Link>
        <Typography color="textPrimary"> Aptitude Topics </Typography>
      </Breadcrumbs>

      {/* Title */}
      <Typography variant="h4" sx={{ mb: 3, fontWeight: "bold", color: "#e65100" }}>
        Aptitude Questions and Answers
      </Typography>
      
      <Grid container spacing={3}>
        {/* Left Section (Topics List) */}
        <Grid item xs={12} md={9}>
          <Paper sx={{ p: 3 }} elevation={3}>
            {/* Search Filter */}
            <TextField fullWidth variant="outlined" placeholder="ðŸ” Search topics..." sx={{ mb: 3 }} />

            {/* Topics Grid */}
            <Grid container spacing={2}>
              {topics.map((topic, index) => (
                <Grid
                  item xs={12} sm={6} md={4} key={index}
                  onClick={() => navigate(`/topic/${topic.toLowerCase().replace(/\s+/g, "-")}`)}
                >
                  <Box
                    sx={{
                      display: "flex",
                      alignItems: "center",
                      cursor: "pointer",
                      p: 1,
                      transition: "0.3s",
                      borderRadius: 1,
                      '&:hover': { color: "blue", backgroundColor: "#f5f5f5" }
                    }}
                  >
                    <Folder sx={{ color: "#e65100", mr: 1 }} />
                    <Typography>{topic}</Typography>
                  </Box>
                </Grid>
              ))}
            </Grid>
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
};

export default TopicPage;
