import React from "react";
import { 
  Container, Grid, Paper, Typography, Breadcrumbs, Link, TextField, Box, Divider 
} from "@mui/material";
import { Folder } from "@mui/icons-material";
import { useParams, useNavigate } from "react-router-dom";

const topics = [
  "Problems on Trains", "Simple Interest", "Percentage", "Average", "Numbers",
  "Simplification", "Chain Rule", "Logarithm", "True Discount", "Time and Distance",
  "Compound Interest", "Problems on Ages", "Area", "Problems on Numbers",
  "Square Root and Cube Root", "Pipes and Cistern", "Races and Games", "Banker’s Discount",
  "Height and Distance", "Profit and Loss", "Calendar", "Clock", "Volume and Surface Area",
  "Permutation and Combination", "Problems on H.C.F and L.C.M", "Surds and Indices",
  "Boats and Streams", "Stocks and Shares", "Odd Man Out and Series", "Time and Work",
  "Partnership", "Decimal Fraction", "Ratio and Proportion", "Alligation or Mixture",
  "Probability"
];

const quickLinks = {
  "Quantitative Aptitude": ["Arithmetic", "Data Interpretation"],
  "Verbal (English)": ["Verbal Ability", "Verbal Test"],
  "Reasoning": ["Logical", "Verbal", "Nonverbal"],
  "Programming": ["Python Programming", "C Programming", "C++, C#", "Java"],
  "Interview": ["GD", "HR", "Technical Interview"],
  "Placement Papers": ["Placement Papers", "Submit Paper"]
};

const TopicPage = () => {
  const { item } = useParams();
  const navigate = useNavigate();
  const formattedItem = item.replace(/-/g, " ");

  return (
    <Container sx={{ mt: 3, mb: 5 }}>
      {/* Breadcrumb Navigation */}
      <Breadcrumbs aria-label="breadcrumb" sx={{ mb: 2 }}>
        <Link underline="hover" color="inherit" href="/">
          Home
        </Link>
        <Link underline="hover" color="inherit" href="/aptitude">
          Aptitude
        </Link>
        <Typography color="textPrimary">List of Topics</Typography>
      </Breadcrumbs>

      {/* Title */}
      <Typography variant="h4" sx={{ mb: 3, fontWeight: "bold", color: "#e65100" }}>
        {formattedItem} Questions and Answers
      </Typography>
      
      <Grid container spacing={3}>
        {/* Left Section (Topics List) */}
        <Grid item xs={12} md={9}>
          <Paper sx={{ p: 3 }} elevation={3}>
            {/* Search Filter */}
            <TextField
              fullWidth
              variant="outlined"
              placeholder="🔍 Search topics..."
              sx={{ mb: 3 }}
            />

            {/* Topics Grid */}
            <Grid container spacing={2}>
              {topics.map((topic, index) => (
                <Grid
                  item xs={12} sm={6} md={4} key={index}
                  onClick={() => navigate(`/topic/${topic.toLowerCase().replace(/\s+/g, "-")}`)}
                >
                  <Box
                    sx={{
                      display: "flex",
                      alignItems: "center",
                      cursor: "pointer",
                      p: 1,
                      transition: "0.3s",
                      borderRadius: 1,
                      '&:hover': { color: "blue", backgroundColor: "#f5f5f5" }
                    }}
                  >
                    <Folder sx={{ color: "#e65100", mr: 1 }} />
                    <Typography>{topic}</Typography>
                  </Box>
                </Grid>
              ))}
            </Grid>
          </Paper>
        </Grid>

        {/* Right Section (Widgets) */}
        <Grid item xs={12} md={3}>
          {/* Current Affairs Widget */}
          <Paper sx={{ p: 2, mb: 3 }} elevation={3}>
            <Typography variant="h6" sx={{ fontWeight: "bold", display: "flex", alignItems: "center" }}>
              ⏳ Current Affairs
            </Typography>
            <Typography variant="body2" sx={{ mt: 1 }}>
              Stay updated with the latest current affairs questions and answers.
            </Typography>
          </Paper>

          {/* Quick Links Widget */}
          <Paper sx={{ p: 2 }} elevation={3}>
            <Typography variant="h6" sx={{ fontWeight: "bold" }}>Quick Links</Typography>
            <Divider sx={{ my: 1 }} />
            {Object.entries(quickLinks).map(([category, links], index) => (
              <Box key={index} sx={{ mt: 2 }}>
                <Typography sx={{ fontWeight: "bold", color: "#e65100" }}>{category}</Typography>
                {links.map((link, i) => (
                  <Typography
                    key={i}
                    sx={{ fontSize: "14px", cursor: "pointer", mt: 0.5, '&:hover': { color: "blue" } }}
                    onClick={() => navigate(`/topic/${link.toLowerCase().replace(/\s+/g, "-")}`)}
                  >
                    › {link}
                  </Typography>
                ))}
              </Box>
            ))}
          </Paper>
        </Grid>
      </Grid>

      {/* Footer Section */}
      <Typography variant="body2" sx={{ textAlign: "center", mt: 4, color: "gray" }}>
        © AptitudeArc Technologies | 
        <Link href="#" sx={{ ml: 1 }}>Contact Us</Link> | 
        <Link href="#" sx={{ ml: 1 }}>Copyright</Link> | 
        <Link href="#" sx={{ ml: 1 }}>Privacy Policy</Link>
      </Typography>
    </Container>
  );
};

export default TopicPage;
