import React, { useEffect, useState } from "react";
import axios from "axios";
import { FaBars, FaSearch, FaShareAlt, FaCog, FaSignOutAlt } from "react-icons/fa";
import "../styles/Home.css"; // Ensure you have your styles here

const Home = () => {
  const [categories, setCategories] = useState([]);

  // Fetch categories from the backend API when component mounts
  useEffect(() => {
    axios.get("http://localhost:8080/api/categories")
      .then((response) => {
        setCategories(response.data); // Save the fetched data into state
      })
      .catch((error) => {
        console.error("There was an error fetching the categories!", error);
      });
  }, []);

  return (
    <div>
      {/* Navbar */}
      <nav className="navbar">
        <div className="navbar-left">
          <FaBars className="icon menu-icon" />
          <h2 className="logo">AptitudeArc</h2>
        </div>

        <div className="navbar-center">
          <div className="search-box">
            <FaSearch className="icon search-icon" />
            <input type="text" placeholder="Search" />
          </div>
        </div>

        <div className="navbar-right">
          <FaShareAlt className="icon" />
          <FaCog className="icon" />
          <FaSignOutAlt className="icon" />
        </div>
      </nav>

      {/* Welcome Section */}
      <div className="welcome-section">
        <h2>Welcome to <span className="highlight">IndiaBIX.Com!</span></h2>
        <p>Aptitude questions and answers for your placement interviews and competitive exams!</p>
      </div>

      {/* Grid Section */}
      <div className="grid-container">
        {categories.map((category, index) => (
          <div key={index} className="grid-card">
            <h3>{category}</h3>
            <ul>
              <li>&gt; Example Item 1</li>
              <li>&gt; Example Item 2</li>
              <li>&gt; Example Item 3</li>
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
