import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { Container, Typography, Button, Paper, Box, Grid } from "@mui/material";
import axios from "axios";

const AdminDashboard = () => {
  const navigate = useNavigate();
  const [adminName, setAdminName] = useState("");
  const [error, setError] = useState("");

  useEffect(() => {
    const token = sessionStorage.getItem("token") || localStorage.getItem("token");
    if (!token) {
      navigate("/login");
      return;
    }

    const fetchAdminData = async () => {
      try {
        const response = await axios.get("http://localhost:8080/admin/dashboard", {
          headers: { Authorization: `Bearer ${token}` }
        });
        setAdminName(response.data.username || "Admin");
      } catch (error) {
        setError("Session expired. Please log in again.");
        setTimeout(() => navigate("/login"), 2000); // Redirect after 2s
      }
    };

    fetchAdminData();
  }, [navigate]);

  const handleLogout = () => {
    if (window.confirm("Are you sure you want to log out?")) {
      sessionStorage.clear();
      localStorage.clear();
      navigate("/login");
    }
  };

  return (
    <Container maxWidth="md">
      <Box sx={{ display: "flex", justifyContent: "center", mt: 5 }}>
        <Paper elevation={3} sx={{ p: 4, textAlign: "center", width: "100%" }}>
          <Typography variant="h4" gutterBottom>Admin Dashboard</Typography>
          <Typography variant="h5" color="primary">Welcome, {adminName}!</Typography>
          <Typography variant="body1" sx={{ mt: 2 }}>
            {error ? <Typography color="error">{error}</Typography> : "Manage your platform efficiently."}
          </Typography>

          {/* Admin Navigation Buttons */}
          <Grid container spacing={2} sx={{ mt: 3, justifyContent: "center" }}>
            <Grid item xs={12} sm={6}>
              <Button
                variant="contained"
                fullWidth
                color="secondary"
                onClick={() => navigate("/admin/user-management")}
              >
                User Management
              </Button>
            </Grid>
            <Grid item xs={12} sm={6}>
              <Button
                variant="contained"
                fullWidth
                color="secondary"
                onClick={() => navigate("/admin/test-management")}
              >
                Test Management
              </Button>
            </Grid>
            <Grid item xs={12} sm={6}>
              <Button
                variant="contained"
                fullWidth
                color="secondary"
                onClick={() => navigate("/admin/analytics")}
              >
                Analytics
              </Button>
            </Grid>
            <Grid item xs={12} sm={6}>
              <Button
                variant="contained"
                fullWidth
                color="error"
                onClick={handleLogout}
              >
                Logout
              </Button>
            </Grid>
          </Grid>
        </Paper>
      </Box>
    </Container>
  );
};

export default AdminDashboard;
