import { useParams } from "react-router-dom";
import { Container, Typography, Card, CardContent, Alert } from "@mui/material";

const CategoryDetail = () => {
  const { categoryId } = useParams();

  // Fetch category details based on categoryId
  const categoryDetails = {
    "general-aptitude": {
      title: "General Aptitude",
      description: "Detailed information about General Aptitude.",
    },
    "verbal-reasoning": {
      title: "Verbal and Reasoning",
      description: "Detailed information about Verbal and Reasoning.",
    },
    // Add other categories as needed
  };

  const category = categoryDetails[categoryId];

  if (!category) {
    return (
      <Container maxWidth="sm" sx={{ mt: 4 }}>
        <Alert severity="error">Category not found.</Alert>
      </Container>
    );
  }

  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <Card>
        <CardContent>
          <Typography variant="h4" gutterBottom>
            {category.title}
          </Typography>
          <Typography variant="body1">{category.description}</Typography>
        </CardContent>
      </Card>
    </Container>
  );
};

export default CategoryDetail;
