import React, { useEffect, useState } from "react";
import {
  Container, Typography, Breadcrumbs, Link, Paper, FormControl, FormControlLabel,
  RadioGroup, Radio, Button, Divider, CircularProgress
} from "@mui/material";
import { useParams } from "react-router-dom";
import axios from "axios";

// Default questions for different topics
const defaultQuestionsData = {
  "problems-on-trains": [
    {
      id: 1,
      question: "A train running at 60 km/hr crosses a pole in 9 seconds. What is the length of the train?",
      options: ["120 meters", "180 meters", "324 meters", "150 meters"],
    },
    {
      id: 2,
      question: "A train 125 meters long passes a man running at 5 km/hr in the same direction in 10 seconds. What is the train’s speed?",
      options: ["45 km/hr", "50 km/hr", "54 km/hr", "55 km/hr"],
    },
    {
      id: 3,
      question: "The length of a bridge, which a train 130 meters long and traveling at 45 km/hr can cross in 30 seconds, is:",
      options: ["200 meters", "225 meters", "245 meters", "250 meters"],
    },
  ],
  "Simple Interest" : [
    {
      "id": 1,
      "question": "What will be the simple interest on $1000 for 2 years at an annual rate of 5%?",
      "options": ["$50", "$100", "$150", "$200"]
    },
    {
      "id": 2,
      "question": "A sum of $5000 is invested at an annual simple interest rate of 8%. What will be the interest earned in 3 years?",
      "options": ["$800", "$1000", "$1200", "$1500"]
    },
    {
      "id": 3,
      "question": "If the simple interest on a certain sum for 4 years at 6% per annum is $480, what is the principal amount?",
      "options": ["$1800", "$2000", "$2200", "$2500"]
    },
    {
      "id": 4,
      "question": "In how many years will a sum of $3000 become $3600 at a simple interest rate of 5% per annum?",
      "options": ["3 years", "4 years", "5 years", "6 years"]
    },
    {
      "id": 5,
      "question": "At what rate of interest per annum will $4000 yield a simple interest of $640 in 4 years?",
      "options": ["2%", "3%", "4%", "5%"]
    }
  ],
  "Percenteage": [
    {
      "id": 1,
      "question": "A student scored 72 marks in an exam out of 90. What is the percentage of marks obtained?",
      "options": ["75%", "80%", "85%", "90%"]
    },
    {
      "id": 2,
      "question": "A number is increased by 20% and then decreased by 20%. What is the overall change in percentage?",
      "options": ["No change", "4% decrease", "4% increase", "2% decrease"]
    },
    {
      "id": 3,
      "question": "A salary is increased by 25% and then decreased by 20%. What is the net percentage increase or decrease?",
      "options": ["No change", "2% increase", "5% increase", "10% increase"]
    },
    {
      "id": 4,
      "question": "A man spends 40% of his income on rent and 30% on other expenses. If his total income is $5000, how much does he save?",
      "options": ["$1000", "$1500", "$2000", "$2500"]
    },
    {
      "id": 5,
      "question": "The population of a town increases by 10% annually. If the current population is 50,000, what will be the population after one year?",
      "options": ["52,000", "55,000", "55,500", "51,000"]
    }
  ],
  "Numbers" : [
    {
      "id": 1,
      "question": "What is the smallest number that is exactly divisible by 6, 8, and 12?",
      "options": ["12", "24", "48", "36"]
    },
    {
      "id": 2,
      "question": "What is the largest two-digit number that is divisible by both 3 and 5?",
      "options": ["90", "95", "99", "85"]
    },
    {
      "id": 3,
      "question": "Find the missing number in the series: 3, 6, 9, __, 15.",
      "options": ["10", "11", "12", "13"]
    },
    {
      "id": 4,
      "question": "What is the sum of the first 10 natural numbers?",
      "options": ["45", "50", "55", "60"]
    },
    {
      "id": 5,
      "question": "A number is doubled and then increased by 5. The result is 29. What is the original number?",
      "options": ["10", "11", "12", "15"]
    }
  ],
};

const TopicDetail = () => {
  const { topic } = useParams();
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/topics/${topic}`);
        setQuestions(response.data.questions);
      } catch (err) {
        console.error("Backend not connected. Using default data.");
        setError("Backend not connected. Showing sample questions.");
        setQuestions(defaultQuestionsData[topic] || []);
      } finally {
        setLoading(false);
      }
    };

    fetchQuestions();
  }, [topic]);

  return (
    <Container sx={{ mt: 3 }}>
      {/* Breadcrumbs */}
      <Breadcrumbs aria-label="breadcrumb">
        <Link underline="hover" color="inherit" href="/">Home</Link>
        <Link underline="hover" color="inherit" href="/topic">Aptitude</Link>
        <Typography color="textPrimary">{topic.replace(/-/g, " ")}</Typography>
      </Breadcrumbs>

      {/* Topic Title */}
      <Typography variant="h4" sx={{ my: 2, fontWeight: "bold", textTransform: "capitalize" }}>
        {topic.replace(/-/g, " ")} - General Questions
      </Typography>

      {/* Loading & Error Handling */}
      {loading ? <CircularProgress sx={{ display: "block", mx: "auto", my: 3 }} /> : null}
      {error ? <Typography color="error">{error}</Typography> : null}

      {/* Questions List */}
      {!loading && questions.length > 0 ? (
        questions.map((q, index) => (
          <Paper key={q.id} sx={{ p: 3, mb: 2 }} elevation={3}>
            <Typography variant="h6">{index + 1}. {q.question}</Typography>
            <FormControl component="fieldset">
              <RadioGroup>
                {q.options.map((option, i) => (
                  <FormControlLabel key={i} value={option} control={<Radio />} label={option} />
                ))}
              </RadioGroup>
            </FormControl>
            <Button variant="contained" color="primary" sx={{ mt: 2 }}>Check Answer</Button>
          </Paper>
        ))
      ) : (
        !loading && <Typography>No questions available for this topic.</Typography>
      )}

      {/* Footer */}
      <Divider sx={{ my: 3 }} />
      <Typography variant="body2" sx={{ textAlign: "center", color: "gray" }}>
        © AptitudeArc Technologies |
        <Link href="#" sx={{ ml: 1 }}>Contact Us</Link> |
        <Link href="#" sx={{ ml: 1 }}>Privacy Policy</Link>
      </Typography>
    </Container>
  );
};

export default TopicDetail;
