import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  Container,
  Typography,
  Button,
  Paper,
  CircularProgress,
  Alert,
  Avatar,
  Grid,
  Card,
  CardContent,
  IconButton,
  Box,
} from "@mui/material";
import EditIcon from "@mui/icons-material/Edit";
import axios from "axios";

const defaultUserData = {
  username: "Guest User",
  email: "guest@example.com",
  role: "User",
  mobile: "Not provided",
  gender: "Not provided",
  dob: "Not provided",
  city: "Not provided",
  activities: [
    { type: "Visited Home Page", time: "Today" },
    { type: "Browsed Topics", time: "Yesterday" },
  ],
};

const ProfilePage = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const token = sessionStorage.getItem("token") || localStorage.getItem("token");

    if (!token) {
      setError("Session expired. Redirecting to login...");
      setTimeout(() => navigate("/login"), 2000);
      return;
    }

    axios
      .get("http://localhost:8080/user/profile", {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((response) => {
        setUser(response.data);
      })
      .catch((error) => {
        console.error("Error fetching user data:", error.response?.data || error.message);
        setError("Failed to load profile. Using default data.");
        setUser(defaultUserData);
      })
      .finally(() => setLoading(false));
  }, [navigate]);

  const handleLogout = () => {
    sessionStorage.clear();
    localStorage.clear();
    navigate("/login");
  };

  return (
    <Container maxWidth="md" sx={{ mt: 5 }}>
      <Paper elevation={3} sx={{ p: 4 }}>
        {loading ? (
          <Box textAlign="center">
            <CircularProgress />
          </Box>
        ) : (
          <>
            {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
            <Grid container spacing={3} alignItems="center">
              <Grid item xs={12} sm={4} textAlign="center">
                <Avatar sx={{ width: 90, height: 90, mx: "auto" }} />
                <Typography variant="h6" sx={{ mt: 1, fontWeight: "bold" }}>{user?.username}</Typography>
                <Typography variant="body2" color="textSecondary">{user?.role}</Typography>
              </Grid>

              <Grid item xs={12} sm={8}>
                <Card elevation={2}>
                  <CardContent>
                    <Typography variant="h6" gutterBottom>Personal Details</Typography>
                    <Typography><strong>Email:</strong> {user?.email}</Typography>
                    <Typography><strong>Mobile:</strong> {user?.mobile}</Typography>
                    <Typography><strong>Gender:</strong> {user?.gender}</Typography>
                    <Typography><strong>DOB:</strong> {user?.dob}</Typography>
                    <Typography><strong>City:</strong> {user?.city}</Typography>
                    <IconButton size="small" sx={{ mt: 1 }}>
                      <EditIcon fontSize="small" />
                    </IconButton>
                  </CardContent>
                </Card>
              </Grid>
            </Grid>

            <Typography variant="h6" sx={{ mt: 4, fontWeight: "bold" }}>Recent Activities</Typography>
            <Paper elevation={2} sx={{ p: 2, mt: 1 }}>
              {user?.activities?.length ? (
                user.activities.map((activity, index) => (
                  <Typography key={index}>{`${index + 1}. ${activity.type} - ${activity.time}`}</Typography>
                ))
              ) : (
                <Typography>No recent activities</Typography>
              )}
            </Paper>

            <Button variant="contained" color="primary" fullWidth sx={{ mt: 3 }} onClick={handleLogout}>
              Logout
            </Button>
          </>
        )}
      </Paper>
    </Container>
  );
};

export default ProfilePage;
