import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import {
  AppBar,
  Toolbar,
  IconButton,
  Typography,
  Drawer,
  List,
  ListItem,
  ListItemButton,
  ListItemText,
  InputBase,
  Paper,
  Grid,
  Card,
  CardContent,
  CssBaseline,
  useTheme,
  ThemeProvider,
  createTheme,
} from "@mui/material";
import {
  Menu as MenuIcon,
  Search as SearchIcon,
  Share as ShareIcon,
  Brightness4 as DarkModeIcon,
  Brightness7 as LightModeIcon,
  ExitToApp as LogoutIcon,
} from "@mui/icons-material";

const Home = () => {
  const navigate = useNavigate();
  const [isDarkMode, setIsDarkMode] = useState(() => {
    const savedMode = localStorage.getItem("isDarkMode");
    return savedMode === "true" || (savedMode === null && window.matchMedia("(prefers-color-scheme: dark)").matches);
  });
  const [isSidebarVisible, setIsSidebarVisible] = useState(false);

  const categories = [
    { title: "General Aptitude", id: "general-aptitude", items: ["Arithmetic", "DataInterpretation"] },
    { title: "Verbal and Reasoning", id: "verbal-reasoning", items: ["VerbalAbility", "LogicalReasoning"] },
  ];

  const handleItemClick = (categoryId, itemName) => {
    navigate(`/category/${categoryId}/item/${itemName}`);
  };

  const handleShare = () => {
    const currentUrl = window.location.href;
    if (navigator.share) {
      navigator
        .share({ title: "AptitudeArc", text: "Check out this amazing site!", url: currentUrl })
        .then(() => console.log("Share successful"))
        .catch((error) => console.error("Error sharing:", error));
    } else {
      const shareUrl = `https://social-plugins.line.me/lineit/share?url=${encodeURIComponent(currentUrl)}`;
      window.open(shareUrl, "_blank", "noopener,noreferrer,width=550,height=550");
    }
  };

  const toggleBrightness = () => {
    setIsDarkMode((prevMode) => {
      const newMode = !prevMode;
      localStorage.setItem("isDarkMode", newMode);
      return newMode;
    });
  };

  const toggleSidebar = () => {
    setIsSidebarVisible((prev) => !prev);
  };

  const handleLogout = () => {
    localStorage.removeItem("authToken");
    localStorage.removeItem("user");
    navigate("/login");
  };

  useEffect(() => {
    if (isDarkMode) {
      document.body.classList.add("dark-mode");
    } else {
      document.body.classList.remove("dark-mode");
    }
  }, [isDarkMode]);

  // Material-UI Theme
  const theme = createTheme({
    palette: { mode: isDarkMode ? "dark" : "light" },
  });

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <AppBar position="static">
        <Toolbar>
          {/* Sidebar Toggle */}
          <IconButton edge="start" color="inherit" onClick={toggleSidebar}>
            <MenuIcon />
          </IconButton>

          {/* Title */}
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            AptitudeArc
          </Typography>

          {/* Search Box */}
          <Paper sx={{ display: "flex", alignItems: "center", width: 250, mr: 2, pl: 1 }}>
            <InputBase placeholder="Search..." fullWidth />
            <IconButton>
              <SearchIcon />
            </IconButton>
          </Paper>

          {/* Actions */}
          <IconButton color="inherit" onClick={handleShare}>
            <ShareIcon />
          </IconButton>
          <IconButton color="inherit" onClick={toggleBrightness}>
            {isDarkMode ? <LightModeIcon /> : <DarkModeIcon />}
          </IconButton>
          <IconButton color="inherit" onClick={handleLogout}>
            <LogoutIcon />
          </IconButton>
        </Toolbar>
      </AppBar>

      {/* Sidebar (Drawer) */}
      <Drawer anchor="left" open={isSidebarVisible} onClose={toggleSidebar}>
        <List>
          {categories.map((category, index) => (
            <ListItem key={index} disablePadding>
              <ListItemButton>
                <ListItemText primary={category.title} />
              </ListItemButton>
            </ListItem>
          ))}
        </List>
      </Drawer>

      {/* Welcome Section */}
      <Typography variant="h4" align="center" sx={{ mt: 3 }}>
        Welcome to <span style={{ color: "#1976d2" }}>AptitudeArc</span>
      </Typography>
      <Typography variant="body1" align="center" sx={{ mb: 3 }}>
        Aptitude questions and answers for placement interviews and competitive exams!
      </Typography>

      {/* Grid Layout for Categories */}
      <Grid container spacing={3} justifyContent="center">
        {categories.map((category, index) => (
          <Grid item key={index} xs={12} sm={6} md={4}>
            <Card sx={{ p: 2, textAlign: "center" }}>
              <CardContent>
                <Typography variant="h6">{category.title}</Typography>
                <List>
                  {category.items.map((item, i) => (
                    <ListItem key={i} disablePadding>
                      <ListItemButton onClick={() => handleItemClick(category.id, item)}>
                        <ListItemText primary={item.replace(/([A-Z])/g, " $1").trim()} />
                      </ListItemButton>
                    </ListItem>
                  ))}
                </List>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </ThemeProvider>
  );
};

export default Home;
