import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  Container,
  Typography,
  Button,
  Paper,
  CircularProgress,
  Alert,
  Avatar,
  Grid,
  Card,
  CardContent,
  IconButton,
} from "@mui/material";
import EditIcon from "@mui/icons-material/Edit";
import axios from "axios";

const defaultUserData = {
  username: "Guest User",
  email: "guest@example.com",
  role: "User",
  mobile: "Not provided",
  gender: "Not provided",
  dob: "Not provided",
  city: "Not provided",
  activities: [
    { type: "Visited Home Page", time: "Today" },
    { type: "Browsed Topics", time: "Yesterday" },
  ],
};

const ProfilePage = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const token = sessionStorage.getItem("token");

    if (!token) {
      setError("Session expired. Please log in again.");
      setLoading(false);
      setTimeout(() => navigate("/login"), 2000);
      return;
    }

    axios
      .get("http://localhost:8080/user/profile", {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((response) => {
        setUser(response.data);
      })
      .catch((error) => {
        console.error("Error fetching user data:", error.response?.data || error.message);

        if (error.response?.status === 401) {
          setError("Session expired. Redirecting to login...");
          sessionStorage.clear();
          localStorage.clear();
          setTimeout(() => navigate("/login"), 2000);
        } else {
          setError("Failed to load profile. Using default data.");
          setUser(defaultUserData);
        }
      })
      .finally(() => setLoading(false));
  }, [navigate]);

  const handleLogout = () => {
    sessionStorage.clear();
    localStorage.clear();
    navigate("/login");
  };

  return (
    <Container maxWidth="md">
      <Paper elevation={3} sx={{ p: 4, mt: 5 }}>
        {loading ? (
          <CircularProgress />
        ) : (
          <>
            {error && <Alert severity="error">{error}</Alert>}
            <Grid container spacing={3}>
              <Grid item xs={12} sm={4} textAlign="center">
                <Avatar sx={{ width: 80, height: 80, margin: "auto" }} />
                <Typography variant="h6" sx={{ mt: 1 }}>{user?.username}</Typography>
                <Typography variant="body2" color="textSecondary">
                  {user?.role}
                </Typography>
              </Grid>

              <Grid item xs={12} sm={8}>
                <Card>
                  <CardContent>
                    <Typography variant="h6">Personal Details</Typography>
                    <Typography>Email: {user?.email}</Typography>
                    <Typography>Mobile: {user?.mobile}</Typography>
                    <Typography>Gender: {user?.gender}</Typography>
                    <Typography>DOB: {user?.dob}</Typography>
                    <Typography>City: {user?.city}</Typography>
                    <IconButton size="small">
                      <EditIcon fontSize="small" />
                    </IconButton>
                  </CardContent>
                </Card>
              </Grid>
            </Grid>

            <Typography variant="h6" sx={{ mt: 4 }}>
              Recent Activities
            </Typography>
            <Paper elevation={2} sx={{ p: 2 }}>
              {user?.activities?.length ? (
                user.activities.map((activity, index) => (
                  <Typography key={index}>{`${index + 1}. ${activity.type} - ${activity.time}`}</Typography>
                ))
              ) : (
                <Typography>No recent activities</Typography>
              )}
            </Paper>

            <Button variant="contained" color="primary" onClick={handleLogout} sx={{ mt: 3 }}>
              Logout
            </Button>
          </>
        )}
      </Paper>
    </Container>
  );
};

export default ProfilePage;
