import React from 'react';
import { useNavigate } from 'react-router-dom';
import { FaBars, FaSearch, FaShareAlt, FaCog, FaSignOutAlt } from 'react-icons/fa';
import Sidebar from './Sidebar';
import '../styles/Home.css';

const Home = () => {
  const navigate = useNavigate();

  const categories = [
    {
      title: 'General Aptitude',
      id: 'general-aptitude',
      items: [
        { name: 'Arithmetic Aptitude', id: 'arithmetic-aptitude' },
        { name: 'Data Interpretation', id: 'data-interpretation' },
        { name: 'Online Aptitude Test', id: 'online-aptitude-test' },
        { name: 'Data Interpretation Test', id: 'data-interpretation-test' },
      ],
    },
    {
      title: 'Verbal and Reasoning',
      id: 'verbal-reasoning',
      items: [
        { name: 'Verbal Ability', id: 'verbal-ability' },
        { name: 'Logical Reasoning', id: 'logical-reasoning' },
        { name: 'Verbal Reasoning', id: 'verbal-reasoning' },
        { name: 'Non-Verbal Reasoning', id: 'non-verbal-reasoning' },
      ],
    },
    // Add other categories as needed
  ];

  const handleItemClick = (categoryId, itemId) => {
    navigate(`/category/${categoryId}/item/${itemId}`);
  };

  return (
    <div className="home-container">
      <Sidebar />
      <div className="main-content">
        <nav className="navbar">
          <div className="navbar-left">
            <FaBars className="icon menu-icon" />
            <h2 className="logo">AptitudeArc</h2>
          </div>
          <div className="navbar-center">
            <div className="search-box">
              <input type="text" className="search-input" placeholder="Search categories..." />
              <FaSearch className="icon search-icon" />
            </div>
          </div>
          <div className="navbar-right">
            <button className="icon-button">
              <FaShareAlt className="icon" />
            </button>
            <button className="icon-button">
              <FaCog className="icon" />
            </button>
            <button className="icon-button">
              <FaSignOutAlt className="icon" />
            </button>
          </div>
        </nav>
        <div className="welcome-section">
          <h2>
            Welcome to <span className="highlight">AptitudeArc</span>
          </h2>
          <p>Aptitude questions and answers for your placement interviews and competitive exams!</p>
        </div>
        <div className="grid-container">
          {categories.map((category, index) => (
            <div key={index} className="grid-card">
              <h3>{category.title}</h3>
              <ul>
                {category.items.map((item, i) => (
                  <li key={i} onClick={() => handleItemClick(category.id, item.id)}>
                    {item.name}
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Home;
