import React from "react";
import { Container, Grid, Paper, Typography, Box } from "@mui/material";

const categories = [
  { title: "General Aptitude", items: ["Arithmetic Aptitude", "Data Interpretation", "Online Tests"] },
  { title: "Interview", items: ["Placement Papers", "Group Discussion", "HR Interview"] },
  { title: "Engineering", items: ["Mechanical", "Civil", "CSE", "EEE"] },
  { title: "Programming", items: ["C", "C++", "Java", "Python"] },
  { title: "Current Affairs & GK", items: ["Current Affairs", "General Knowledge", "General Science"] },
  { title: "Logical Reasoning", items: ["Verbal Reasoning", "Non-Verbal Reasoning", "Puzzles"] },
];

const Home = () => {
  return (
    <Container sx={{ mt: 4 }}>
      <Typography variant="h4" fontWeight="bold" sx={{ mb: 3, textAlign: "center", color: "#2e7d32" }}>
        Welcome to AptitudeArc!
      </Typography>
      <Typography variant="body1" sx={{ textAlign: "center", mb: 3 }}>
        Prepare for your placement interviews and competitive exams with high-quality questions and answers!
      </Typography>

      <Grid container spacing={3} justifyContent="center">
        {categories.map((category, index) => (
          <Grid item xs={12} sm={6} md={4} key={index}>
            <Paper
              elevation={3}
              sx={{
                padding: 2,
                backgroundColor: "#f9f9f9",
                borderRadius: 2,
                transition: "0.3s",
                "&:hover": { transform: "scale(1.03)", boxShadow: 6 },
              }}
            >
              <Typography variant="h6" sx={{ color: "#1b5e20", fontWeight: "bold", mb: 1 }}>
                {category.title}
              </Typography>
              <Box component="ul" sx={{ paddingLeft: 2, margin: 0 }}>
                {category.items.map((item, i) => (
                  <Typography component="li" key={i} sx={{ fontSize: "1rem", color: "#333" }}>
                    {item}
                  </Typography>
                ))}
              </Box>
            </Paper>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default Home;
