import React from "react";
import "../styles/Arithmetic.css"; // Import the CSS file
import { FaFolder } from "react-icons/fa"; // Importing folder icon

const arithmeticTopics = [
  "Problems on Trains",
  "Simple Interest",
  "Percentage",
  "Average",
  "Numbers",
  "Simplification",
  "Chain Rule",
  "Logarithm",
  "True Discount",
  "Time and Distance",
  "Compound Interest",
  "Problems on Ages",
  "Problems on Numbers",
  "Square Root and Cube Root",
  "Pipes and Cistern",
  "Races and Games",
  "Banker’s Discount"
];

const handleClick = (topic) => {
  alert(`You clicked on ${topic}`);
};

const Arithmetic = () => {
  return (
    <div className="container">
      <h1 className="heading">Arithmetic Topics</h1>
      <div className="grid-container">
        {arithmeticTopics.map((topic, index) => (
          <div key={index} className="topic-card" onClick={() => handleClick(topic)}>
            <FaFolder className="folder-icon" />
            <span>{topic}</span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Arithmetic;
