import React from "react";
import { useParams } from "react-router-dom";
import { Box, Typography } from "@mui/material";

const categories = {
  "general-aptitude": { title: "General Aptitude", description: "Arithmetic and logical reasoning questions." },
  "verbal-reasoning": { title: "Verbal & Reasoning", description: "Verbal ability and logical reasoning exercises." },
  "current-affairs": { title: "Current Affairs & GK", description: "Stay updated with the latest news." },
  "engineering": { title: "Engineering", description: "Mechanical, Civil, and Electrical Engineering topics." },
  "programming": { title: "Programming", description: "C, C++, Java, Python programming challenges." },
};

const CategoryPage = () => {
  const { id } = useParams();
  const category = categories[id];

  if (!category) return <Typography>Category not found.</Typography>;

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" sx={{ color: "green", fontWeight: "bold" }}>
        {category.title}
      </Typography>
      <Typography variant="body1">{category.description}</Typography>
    </Box>
  );
};

export default CategoryPage;
