import React from "react";
import { Grid, Card, CardContent, Typography } from "@mui/material";
import { Link } from "react-router-dom";

const categories = [
  { id: "general-aptitude", title: "General Aptitude", topics: ["Arithmetic Aptitude", "Data Interpretation"] },
  { id: "verbal-reasoning", title: "Verbal & Reasoning", topics: ["Verbal Ability", "Logical Reasoning"] },
  { id: "current-affairs", title: "Current Affairs & GK", topics: ["Current Affairs", "General Knowledge"] },
  { id: "engineering", title: "Engineering", topics: ["Mechanical", "Civil", "Electrical"] },
  { id: "programming", title: "Programming", topics: ["C", "C++", "Java"] },
];

const Home = () => {
  return (
    <Grid container spacing={3} sx={{ mt: 8 }}>
      {categories.map((category) => (
        <Grid item xs={12} sm={6} md={4} key={category.id}>
          <Card component={Link} to={`/category/${category.id}`} sx={{ textDecoration: "none" }}>
            <CardContent>
              <Typography variant="h6" sx={{ fontWeight: "bold", color: "green" }}>
                {category.title}
              </Typography>
              {category.topics.map((topic) => (
                <Typography key={topic} variant="body2">
                  {topic}
                </Typography>
              ))}
            </CardContent>
          </Card>
        </Grid>
      ))}
    </Grid>
  );
};

export default Home;
