import React from "react";
import { Container, Grid, Paper, Typography, Box } from "@mui/material";
import SchoolIcon from "@mui/icons-material/School";
import WorkIcon from "@mui/icons-material/Work";
import EngineeringIcon from "@mui/icons-material/Engineering";
import CodeIcon from "@mui/icons-material/Code";
import PublicIcon from "@mui/icons-material/Public";
import PsychologyIcon from "@mui/icons-material/Psychology";

const categories = [
  { title: "General Aptitude", icon: <SchoolIcon fontSize="large" />, items: ["Arithmetic Aptitude", "Data Interpretation", "Online Tests"] },
  { title: "Interview", icon: <WorkIcon fontSize="large" />, items: ["Placement Papers", "Group Discussion", "HR Interview"] },
  { title: "Engineering", icon: <EngineeringIcon fontSize="large" />, items: ["Mechanical", "Civil", "CSE", "EEE"] },
  { title: "Programming", icon: <CodeIcon fontSize="large" />, items: ["C", "C++", "Java", "Python"] },
  { title: "Current Affairs & GK", icon: <PublicIcon fontSize="large" />, items: ["Current Affairs", "General Knowledge", "General Science"] },
  { title: "Logical Reasoning", icon: <PsychologyIcon fontSize="large" />, items: ["Verbal Reasoning", "Non-Verbal Reasoning", "Puzzles"] },
];

const Home = () => {
  return (
    <Container sx={{ mt: 4 }}>
      {/* Header Section */}
      <Typography variant="h4" fontWeight="bold" sx={{ mb: 3, textAlign: "center", color: "#2e7d32" }}>
        Welcome to AptitudeArc!
      </Typography>
      <Typography variant="body1" sx={{ textAlign: "center", mb: 4, maxWidth: "600px", margin: "auto" }}>
        Prepare for your placement interviews and competitive exams with high-quality questions and answers!
      </Typography>

      {/* Category Grid */}
      <Grid container spacing={3} justifyContent="center">
        {categories.map((category, index) => (
          <Grid item xs={12} sm={6} md={4} key={index}>
            <Paper
              elevation={6}
              sx={{
                padding: 3,
                backgroundColor: "#f9f9f9",
                borderRadius: 3,
                transition: "0.3s",
                textAlign: "center",
                "&:hover": { transform: "scale(1.05)", boxShadow: 8, backgroundColor: "#e8f5e9" },
              }}
            >
              {/* Icon and Title */}
              <Box sx={{ display: "flex", justifyContent: "center", alignItems: "center", flexDirection: "column", mb: 1 }}>
                <Box sx={{ color: "#2e7d32", mb: 1 }}>{category.icon}</Box>
                <Typography variant="h6" sx={{ color: "#1b5e20", fontWeight: "bold" }}>
                  {category.title}
                </Typography>
              </Box>

              {/* List Items */}
              <Box component="ul" sx={{ paddingLeft: 2, margin: 0, listStyle: "none" }}>
                {category.items.map((item, i) => (
                  <Typography component="li" key={i} sx={{ fontSize: "1rem", color: "#333", textAlign: "center", mb: 0.5 }}>
                    {item}
                  </Typography>
                ))}
              </Box>
            </Paper>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default Home;
