import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import {
  Drawer,
  List,
  ListItem,
  ListItemButton,
  ListItemText,
  IconButton,
  Toolbar,
  Typography,
  Divider,
} from "@mui/material";
import {
  Menu as MenuIcon,
  Home as HomeIcon,
  Calculate as AptitudeIcon,
  Work as InterviewIcon,
  Code as ProgrammingIcon,
  School as EngineeringIcon,
  TrendingUp as DataIcon,
  Book as KnowledgeIcon,
} from "@mui/icons-material";
import logo from "../assets/AptitudeArc.jpg";

const Sidebar = () => {
  const [open, setOpen] = useState(false);
  const toggleDrawer = () => setOpen(!open);

  const menuItems = [
    { text: "Home", icon: <HomeIcon />, path: "/" },
    { text: "General Aptitude", icon: <AptitudeIcon />, path: "/general-aptitude" },
    { text: "Interview", icon: <InterviewIcon />, path: "/interview" },
    { text: "Engineering", icon: <EngineeringIcon />, path: "/engineering" },
    { text: "Programming", icon: <ProgrammingIcon />, path: "/programming" },
    { text: "Arithmetic Aptitude", icon: <AptitudeIcon />, path: "/arithmetic" },
    { text: "Data Interpretation", icon: <DataIcon />, path: "/data-interpretation" },
    { text: "Verbal Ability", icon: <KnowledgeIcon />, path: "/verbal-ability" },
    { text: "Logical Reasoning", icon: <KnowledgeIcon />, path: "/logical-reasoning" },
    { text: "General Knowledge", icon: <KnowledgeIcon />, path: "/general-knowledge" },
  ];

  return (
    <>
      <IconButton color="inherit" onClick={toggleDrawer} sx={{ ml: 2 }}>
        <MenuIcon />
      </IconButton>
      <Drawer anchor="left" open={open} onClose={toggleDrawer}>
        <Toolbar sx={{ justifyContent: "center", padding: 2 }}>
          <img src={logo} alt="AptitudeArc Logo" width={120} />
        </Toolbar>
        <Divider />
        <List>
          {menuItems.map((item, index) => (
            <ListItem key={index} disablePadding>
              <ListItemButton component={NavLink} to={item.path} onClick={toggleDrawer}>
                {item.icon && <item.icon sx={{ mr: 2 }} />}
                <ListItemText primary={item.text} />
              </ListItemButton>
            </ListItem>
          ))}
        </List>
      </Drawer>
    </>
  );
};

export default Sidebar;
