import React from "react";
import {
  Container,
  Typography,
  Paper,
  Grid,
  Card,
  CardContent,
  Box,
} from "@mui/material";

const AdminDashboard = () => {
  // Handle the click event for each card item
  const handleCardClick = (item) => {
    console.log(`Clicked on ${item}`);
    // You can implement further logic here, such as navigating to a detailed page
    // For example: navigate(`/admin/${item.toLowerCase().replace(/\\s/g, '-')}`);
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 5 }}>
      <Paper
        elevation={4}
        sx={{
          p: 4,
          textAlign: "center",
          borderRadius: 2,
          backgroundColor: "#fff3e0", // Light orange background
        }}
      >
        <Typography
          variant="h4"
          sx={{ mb: 3, fontWeight: "bold", color: "#e65100" }}
        >
          Admin Dashboard
        </Typography>
        <Grid container spacing={3}>
          <Grid item xs={12} md={4}>
            <Card
              onClick={() => handleCardClick("User Management")}
              sx={{
                border: "2px solid #e65100",
                borderRadius: 2,
                transition: "transform 0.2s, box-shadow 0.2s",
                cursor: "pointer",
                "&:hover": {
                  transform: "scale(1.02)",
                  boxShadow: "0px 4px 12px rgba(230, 81, 0, 0.4)",
                },
              }}
            >
              <CardContent>
                <Box sx={{ mb: 1 }}>
                  <Typography
                    variant="h6"
                    sx={{ color: "#e65100", fontWeight: "bold" }}
                  >
                    User Management
                  </Typography>
                </Box>
                <Typography variant="body2" color="text.secondary">
                  Manage user accounts, permissions, and roles.
                </Typography>
              </CardContent>
            </Card>
          </Grid>
          <Grid item xs={12} md={4}>
            <Card
              onClick={() => handleCardClick("Test Management")}
              sx={{
                border: "2px solid #e65100",
                borderRadius: 2,
                transition: "transform 0.2s, box-shadow 0.2s",
                cursor: "pointer",
                "&:hover": {
                  transform: "scale(1.02)",
                  boxShadow: "0px 4px 12px rgba(230, 81, 0, 0.4)",
                },
              }}
            >
              <CardContent>
                <Box sx={{ mb: 1 }}>
                  <Typography
                    variant="h6"
                    sx={{ color: "#e65100", fontWeight: "bold" }}
                  >
                    Test Management
                  </Typography>
                </Box>
                <Typography variant="body2" color="text.secondary">
                  Oversee and manage tests, assessments, and evaluations.
                </Typography>
              </CardContent>
            </Card>
          </Grid>
          <Grid item xs={12} md={4}>
            <Card
              onClick={() => handleCardClick("Analytics")}
              sx={{
                border: "2px solid #e65100",
                borderRadius: 2,
                transition: "transform 0.2s, box-shadow 0.2s",
                cursor: "pointer",
                "&:hover": {
                  transform: "scale(1.02)",
                  boxShadow: "0px 4px 12px rgba(230, 81, 0, 0.4)",
                },
              }}
            >
              <CardContent>
                <Box sx={{ mb: 1 }}>
                  <Typography
                    variant="h6"
                    sx={{ color: "#e65100", fontWeight: "bold" }}
                  >
                    Analytics
                  </Typography>
                </Box>
                <Typography variant="body2" color="text.secondary">
                  View detailed analytics and insights for better decision making.
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        </Grid>
      </Paper>
    </Container>
  );
};

export default AdminDashboard;
