import React, { useEffect, useState } from "react";
import {
  Container, Typography, Breadcrumbs, Link, Paper, FormControl, FormControlLabel,
  RadioGroup, Radio, Button, Pagination, Box, Divider
} from "@mui/material";
import { useParams } from "react-router-dom";

// Sample data for different topics
const topicData = {
  "problems-on-trains": {
    title: "Problems on Trains",
    questions: [
      {
        id: 1,
        question: "A train running at the speed of 60 km/hr crosses a pole in 9 seconds. What is the length of the train?",
        options: ["120 meters", "180 meters", "324 meters", "150 meters"],
        answer: "150 meters"
      },
      {
        id: 2,
        question: "A train 125 m long passes a man running at 5 km/hr in the same direction in 10 seconds. The speed of the train is:",
        options: ["45 km/hr", "50 km/hr", "54 km/hr", "55 km/hr"],
        answer: "50 km/hr"
      }
    ]
  },
  "simple-interest": {
    title: "Simple Interest",
    questions: [
      {
        id: 1,
        question: "What is the simple interest on $1000 at 5% per annum for 3 years?",
        options: ["$100", "$150", "$200", "$250"],
        answer: "$150"
      }
    ]
  }
};

const TopicDetail = () => {
  const { topic } = useParams();
  const formattedTopic = topic ? topic.replace(/-/g, " ") : "Unknown Topic";

  // Fetch topic-specific data
  const topicInfo = topicData[topic] || { title: formattedTopic, questions: [] };

  return (
    <Container sx={{ mt: 3 }}>
      {/* Breadcrumbs */}
      <Breadcrumbs aria-label="breadcrumb">
        <Link underline="hover" color="inherit" href="/">Home</Link>
        <Link underline="hover" color="inherit" href="/topic/:item">Aptitude</Link>
        <Typography color="textPrimary">{topicInfo.title}</Typography>
      </Breadcrumbs>

      {/* Topic Title */}
      <Typography variant="h4" sx={{ my: 2, fontWeight: "bold", textTransform: "capitalize" }}>
        {topicInfo.title} - General Questions
      </Typography>

      {/* Question List */}
      {topicInfo.questions.length > 0 ? (
        topicInfo.questions.map((q, index) => (
          <Paper key={q.id} sx={{ p: 3, mb: 2 }} elevation={3}>
            <Typography variant="h6">{index + 1}. {q.question}</Typography>
            <FormControl component="fieldset">
              <RadioGroup>
                {q.options.map((option, i) => (
                  <FormControlLabel key={i} value={option} control={<Radio />} label={option} />
                ))}
              </RadioGroup>
            </FormControl>
            <Button variant="contained" color="primary" sx={{ mt: 2 }}>Check Answer</Button>
          </Paper>
        ))
      ) : (
        <Typography>No questions available for this topic.</Typography>
      )}

      {/* Footer Section */}
      <Divider sx={{ my: 3 }} />
      <Typography variant="body2" sx={{ textAlign: "center", color: "gray" }}>
        © AptitudeArc Technologies |
        <Link href="#" sx={{ ml: 1 }}>Contact Us</Link> |
        <Link href="#" sx={{ ml: 1 }}>Privacy Policy</Link>
      </Typography>
    </Container>
  );
};

export default TopicDetail;
